package com.diaspogift.identityandaccess.application.command;import com.diaspogift.identityandaccess.application.representation.UserEmailRepresentation;public class ChangeEmailAddressCommand {    private String tenantId;    private String username;    private String emailAddress;    public ChangeEmailAddressCommand(String tenantId, String username, String emailAddress) {        super();        this.tenantId = tenantId;        this.username = username;        this.emailAddress = emailAddress;    }    public ChangeEmailAddressCommand() {        super();    }    public ChangeEmailAddressCommand(UserEmailRepresentation userEmailRepresentation) {        this.initialiazeFrom(userEmailRepresentation);    }    private void initialiazeFrom(UserEmailRepresentation userEmailRepresentation) {        this.tenantId = userEmailRepresentation.getTenantId();        this.username = userEmailRepresentation.getUsername();        this.emailAddress = userEmailRepresentation.getEmailAddress();    }    public String getTenantId() {        return this.tenantId;    }    public void setTenantId(String tenantId) {        this.tenantId = tenantId;    }    public String getUsername() {        return this.username;    }    public void setUsername(String username) {        this.username = username;    }    public String getEmailAddress() {        return this.emailAddress;    }    public void setEmailAddress(String emailAddress) {        this.emailAddress = emailAddress;    }}