package com.diaspogift.identityandaccess.application.representation.user;import com.diaspogift.identityandaccess.domain.model.identity.TenantId;import com.diaspogift.identityandaccess.domain.model.identity.User;import org.springframework.hateoas.ResourceSupport;public class UserRepresentation extends ResourceSupport {    private String emailAddress;    private boolean enabled;    private String firstName;    private String lastName;    private TenantId tenantId;    private String username;    public UserRepresentation(User aUser) {        super();        this.initializeFrom(aUser);    }    protected UserRepresentation() {        super();    }    public String getEmailAddress() {        return this.emailAddress;    }    public boolean getEnabled() {        return this.enabled;    }    public String getFirstName() {        return this.firstName;    }    public String getLastName() {        return this.lastName;    }    public String getTenantId() {        return this.tenantId.id();    }    public String getUsername() {        return this.username;    }    private void initializeFrom(User aUser) {        this.emailAddress = aUser.person().emailAddress().address();        this.enabled = aUser.isEnabled();        this.firstName = aUser.person().name().firstName();        this.lastName = aUser.person().name().lastName();        this.tenantId = aUser.tenantId();        this.username = aUser.username();    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        if (!super.equals(o)) return false;        UserRepresentation that = (UserRepresentation) o;        if (enabled != that.enabled) return false;        if (emailAddress != null ? !emailAddress.equals(that.emailAddress) : that.emailAddress != null) return false;        if (firstName != null ? !firstName.equals(that.firstName) : that.firstName != null) return false;        if (lastName != null ? !lastName.equals(that.lastName) : that.lastName != null) return false;        if (tenantId != null ? !tenantId.equals(that.tenantId) : that.tenantId != null) return false;        return username != null ? username.equals(that.username) : that.username == null;    }    @Override    public int hashCode() {        int result = super.hashCode();        result = 31 * result + (emailAddress != null ? emailAddress.hashCode() : 0);        result = 31 * result + (enabled ? 1 : 0);        result = 31 * result + (firstName != null ? firstName.hashCode() : 0);        result = 31 * result + (lastName != null ? lastName.hashCode() : 0);        result = 31 * result + (tenantId != null ? tenantId.hashCode() : 0);        result = 31 * result + (username != null ? username.hashCode() : 0);        return result;    }}